import{o as a,c as s,d as n}from"./app.8ddde089.js";const t='{"title":"PHP","description":"","frontmatter":{},"headers":[{"level":2,"title":"Esimene tund","slug":"esimene-tund"},{"level":2,"title":"Esimene kodutöö","slug":"esimene-kodutoo"},{"level":3,"title":"Näita kolme pilti, mis on erinevad","slug":"naita-kolme-pilti-mis-on-erinevad"},{"level":3,"title":"Näita eestikeelset päeva nime.","slug":"naita-eestikeelset-paeva-nime"},{"level":3,"title":"Kuva teadet, kui kuupäeva järgi pole semester veel alanud","slug":"kuva-teadet-kui-kuupaeva-jargi-pole-semester-veel-alanud"}],"relativePath":"php/index.md","lastUpdated":1615961572508}',e={},p=n('<h1 id="php"><a class="header-anchor" href="#php" aria-hidden="true">#</a> PHP</h1><h2 id="esimene-tund"><a class="header-anchor" href="#esimene-tund" aria-hidden="true">#</a> Esimene tund</h2><p>Esimese tunni töö näha siin: <a href="https://github.com/jubejuss/githubactions/blob/main/tund_01.php" target="_blank" rel="noopener noreferrer">https://github.com/jubejuss/githubactions/blob/main/tund_01.php</a></p><p>PHP põhimõtted. Muutujate deklareerimine/defineerimine. Kirjutan ise <code>$muutuja = &quot;Siia kirjutan muutuja&quot;</code><br> Seejärel saan printida muutuja välja. Kokku siis nii:</p><div class="language-php"><pre><code><span class="token php language-php"><span class="token delimiter important">&lt;?</span>\n    <span class="token variable">$muutuja</span> <span class="token operator">=</span> <span class="token string double-quoted-string">&quot;Siia kirjutan muutuja&quot;</span>\n\n    <span class="token keyword">echo</span> <span class="token variable">$muutuja</span>\n<span class="token delimiter important">?&gt;</span></span>\n</code></pre></div><p>või:</p><div class="language-php"><pre><code><span class="token operator">&lt;</span>div <span class="token keyword">class</span><span class="token operator">=</span><span class="token string double-quoted-string">&quot;klass&quot;</span><span class="token operator">&gt;</span><span class="token keyword">echo</span> <span class="token variable">$muutuja</span><span class="token operator">&lt;</span><span class="token operator">/</span>div<span class="token operator">&gt;</span>\n</code></pre></div><p>Loopimisest:</p><div class="language-php"><pre><code><span class="token variable">$picsdir</span> <span class="token operator">=</span> <span class="token string double-quoted-string">&quot;images/&quot;</span><span class="token punctuation">;</span>\n\n<span class="token comment">// määrame, mida otsime, nr 2 lõpus on scandiriga loetud kaks esimest kirjet,</span>\n<span class="token comment">// mis räägivad lihtsalt kataloogist,</span>\n<span class="token variable">$allfiles</span> <span class="token operator">=</span> <span class="token function">array_slice</span><span class="token punctuation">(</span><span class="token function">scandir</span><span class="token punctuation">(</span><span class="token variable">$picsdir</span><span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token number">2</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n<span class="token comment">// for tsükkel et leida vaid pildifailid allfilest ja siis tähista iga võetud</span>\n<span class="token comment">// fail $file. Tsükkel läbitakse niipalju kordi, kui me $allfilesis leidsime</span>\n<span class="token keyword">foreach</span><span class="token punctuation">(</span><span class="token variable">$allfiles</span> <span class="token keyword">as</span> <span class="token variable">$file</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>\n        <span class="token comment">// küsime faili suurust, sest selle abil saame me veel hunniku</span>\n        <span class="token comment">// asju teada just sellelt pildilt, mh failitüübi, mida meil vaja ongi</span>\n\t\t<span class="token variable">$fileinfo</span> <span class="token operator">=</span> <span class="token function">getimagesize</span><span class="token punctuation">(</span><span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$file</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n        <span class="token comment">// kui nüüd fileinfos on &quot;mime&quot; siis edasi</span>\n\t\t<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token keyword">isset</span><span class="token punctuation">(</span><span class="token variable">$fileinfo</span><span class="token punctuation">[</span><span class="token string double-quoted-string">&quot;mime&quot;</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>\n            <span class="token comment">// kui arrays on mime ja kas ta on allowed... massiivis</span>\n\t\t\t<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token function">in_array</span><span class="token punctuation">(</span><span class="token variable">$fileinfo</span><span class="token punctuation">[</span><span class="token string double-quoted-string">&quot;mime&quot;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token variable">$allowedphototypes</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>\n                <span class="token comment">// array_push tähendab  võtan failime ja</span>\n                <span class="token comment">//panen file picfiles massiivi</span>\n\t\t\t\t<span class="token function">array_push</span><span class="token punctuation">(</span><span class="token variable">$picfiles</span><span class="token punctuation">,</span> <span class="token variable">$file</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\t\t\t<span class="token punctuation">}</span>\n\t\t<span class="token punctuation">}</span>\n\t<span class="token punctuation">}</span>\n</code></pre></div><p>Selgitus:<br><code>$picsdir = &quot;images/&quot;;</code> ütleme, kus kataloogis pildid asuvad.</p><p>Ehk, et ma otsisin <code>$allfiles</code> seest asju, mida loeme kui faile ja kui need on sobivas vormingus, lüllame nad picfiles massiivi</p><h2 id="esimene-kodutoo"><a class="header-anchor" href="#esimene-kodutoo" aria-hidden="true">#</a> Esimene kodutöö</h2><p>Täis kood lihtsama lahendusega on siin: <a href="https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_lihtsam.php" target="_blank" rel="noopener noreferrer">https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_lihtsam.php</a><br> ja keerulisemaga siin: <a href="https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_keerulisem.php" target="_blank" rel="noopener noreferrer">https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_keerulisem.php</a><br> Nb! Arvesta, et näidises on kasutatud Bootstrapi, et veebilehele mingi kujundus tekitada, seega võid HTML-i osas kõik klassid lihtsalt kustutada.</p><h3 id="naita-kolme-pilti-mis-on-erinevad"><a class="header-anchor" href="#naita-kolme-pilti-mis-on-erinevad" aria-hidden="true">#</a> Näita kolme pilti, mis on erinevad</h3><p>Ülesanne lisada kolm pilti moel, et need oleks kõik teineteisest erinevad.</p><p>Siin on kaks varianti.</p><h4 id="esiteks-suurema-toomahuga"><a class="header-anchor" href="#esiteks-suurema-toomahuga" aria-hidden="true">#</a> Esiteks suurema töömahuga:</h4><div class="language-php"><pre><code><span class="token variable">$photocount</span> <span class="token operator">=</span> <span class="token function">count</span><span class="token punctuation">(</span><span class="token variable">$picfiles</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\t<span class="token variable">$RandImgArray</span> <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">;</span>\n\n\t<span class="token keyword">do</span> <span class="token punctuation">{</span>\n\t\t<span class="token variable">$RandImg</span> <span class="token operator">=</span> <span class="token function">mt_rand</span><span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token variable">$photocount</span><span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\t\t<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token operator">!</span><span class="token punctuation">(</span><span class="token function">in_array</span><span class="token punctuation">(</span><span class="token variable">$RandImg</span><span class="token punctuation">,</span> <span class="token variable">$RandImgArray</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>\n\t\t\t<span class="token function">array_push</span><span class="token punctuation">(</span><span class="token variable">$RandImgArray</span><span class="token punctuation">,</span> <span class="token variable">$RandImg</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\t\t<span class="token punctuation">}</span>\n\t<span class="token punctuation">}</span> <span class="token keyword">while</span> <span class="token punctuation">(</span><span class="token function">count</span><span class="token punctuation">(</span><span class="token variable">$RandImgArray</span><span class="token punctuation">)</span> <span class="token operator">&lt;</span> <span class="token number">3</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\n\t<span class="token variable">$randomphoto</span> <span class="token operator">=</span> <span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$RandImgArray</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span>\n\t<span class="token variable">$randomphoto2</span> <span class="token operator">=</span> <span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$RandImgArray</span><span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span>\n\t<span class="token variable">$randomphoto3</span> <span class="token operator">=</span> <span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$RandImgArray</span><span class="token punctuation">[</span><span class="token number">2</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span>\n</code></pre></div><p>Selgitus:<br><code>$photocount = count($picfiles);</code> loeme üles piltide arvu <code>$RandImgArray = [];</code> tekitame listi/massiivi, kuhu kogume alljärgnevalt 3 random pilti.</p><p>Kasutame <code>do while funktsiooni</code><br> Loome uue listi/massiivi, kuhu lükkame meile sobivad pildid, st siis need unikaalsed pildid. While counter loendab, palju pilte on ja kui 3 saab täis, on asi valmis.<br><code>$RandImg = mt_rand(0, $photocount-1);</code>leiame esimese suvapildi mt_rand funktsiooniga. Sulgudes on vahemik, millest milleni otsime, ehk 0 kuni viimane pilt (st -1)<br><code>if(!(in_array($RandImg, $RandImgArray)))</code> Kui pilti pole RandImgArray nimelises massiivis/listis, siis:<br><code>array_push($RandImgArray, $RandImg);</code> lükkame ta sinna.</p><p>Kui 3 korda ring peal, defineerime, mis kohal RandImg arrays mingi pilt täpselt on:<br><code>$randomphoto = $picfiles[$RandImgArray[0]];</code><br><code>$randomphoto2 = $picfiles[$RandImgArray[1]];</code><code>$randomphoto3 = $picfiles[$RandImgArray[2]];</code></p><h4 id="ja-teiseks-php-funktsioon"><a class="header-anchor" href="#ja-teiseks-php-funktsioon" aria-hidden="true">#</a> Ja teiseks PHP funktsioon:</h4><div class="language-php"><pre><code><span class="token variable">$randomphotofunc</span> <span class="token operator">=</span> <span class="token function">array_rand</span><span class="token punctuation">(</span><span class="token variable">$picfiles</span><span class="token punctuation">,</span><span class="token number">3</span><span class="token punctuation">)</span><span class="token punctuation">;</span> \n</code></pre></div><p>Kodulehel kuvamiseks, peab esimesel juhul HTML&#39;i osasse kirjutama midagi sellist:</p><div class="language-php"><pre><code>  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$randomphoto</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">/&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$randomphoto2</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">/&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$randomphoto3</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">/&gt;</span></span>\n</code></pre></div><p>Ja teisel juhul midagi sellist:</p><div class="language-php"><pre><code>  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$randomphotofunc</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$randomphotofunc</span><span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span><span class="token php language-php"><span class="token delimiter important">&lt;?php</span> <span class="token keyword">echo</span> <span class="token variable">$picsdir</span> <span class="token operator">.</span><span class="token variable">$picfiles</span><span class="token punctuation">[</span><span class="token variable">$randomphotofunc</span><span class="token punctuation">[</span><span class="token number">2</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">;</span> <span class="token delimiter important">?&gt;</span></span><span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>suvapilt<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><h3 id="naita-eestikeelset-paeva-nime"><a class="header-anchor" href="#naita-eestikeelset-paeva-nime" aria-hidden="true">#</a> Näita eestikeelset päeva nime.</h3><p>Sama lugu sellega – raske, ehk eestlaslik (teadupärast õige eestlane künnab ennast surnuks) ja lihtne, kasutades funktsiooni.</p><h4 id="esmalt-see-eestlaslik"><a class="header-anchor" href="#esmalt-see-eestlaslik" aria-hidden="true">#</a> Esmalt see eestlaslik:</h4><div class="language-php"><pre><code><span class="token variable">$weekday_nr</span><span class="token operator">=</span><span class="token function">date</span><span class="token punctuation">(</span><span class="token string single-quoted-string">&#39;w&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span> \n<span class="token variable">$day_names</span><span class="token operator">=</span><span class="token punctuation">[</span><span class="token string single-quoted-string">&#39;pühapäev&#39;</span><span class="token punctuation">,</span><span class="token string single-quoted-string">&#39;esmaspäev&#39;</span><span class="token punctuation">,</span><span class="token string single-quoted-string">&#39;teisipäev&#39;</span><span class="token punctuation">,</span><span class="token string single-quoted-string">&#39;kolmapäev&#39;</span><span class="token punctuation">,</span>\n<span class="token string single-quoted-string">&#39;neljapäev&#39;</span><span class="token punctuation">,</span><span class="token string single-quoted-string">&#39;reede&#39;</span><span class="token punctuation">,</span><span class="token string single-quoted-string">&#39;laupäev&#39;</span><span class="token punctuation">]</span><span class="token punctuation">;</span>\n<span class="token variable">$todaysweekdayhtml</span><span class="token operator">=</span><span class="token string double-quoted-string">&quot;&lt;p&gt; Täna on &quot;</span><span class="token operator">.</span> <span class="token variable">$day_names</span><span class="token punctuation">[</span><span class="token variable">$weekday_nr</span><span class="token punctuation">]</span><span class="token operator">.</span><span class="token string double-quoted-string">&quot;&lt;/p&gt;&quot;</span><span class="token punctuation">;</span>\n</code></pre></div><p>Selgitus:<br><code>$weekday_nr=date(&#39;w&#39;);</code> PHP funktsioon või nädalapäevade numbriline definitsioon. date(&#39;w&#39;) on leitav PHP manuaalis: <a href="https://www.php.net/manual/en/function.date.php" target="_blank" rel="noopener noreferrer">https://www.php.net/manual/en/function.date.php</a><br><code>$day_names=...</code> ise defineerin listi või array või massiivi päevade nimedega.<br> Ja lõpuks ütleme, et võtku listist tänane päev ja kuvagu seda. Kogu vahepealne osa on lihtsalt jutustus, et täna on ja böa-bla-bla.</p><h4 id="lihtsam-versioon-on-aga-kasutada-lihtsalt-php-funktsiooni"><a class="header-anchor" href="#lihtsam-versioon-on-aga-kasutada-lihtsalt-php-funktsiooni" aria-hidden="true">#</a> Lihtsam versioon on aga kasutada lihtsalt PHP funktsiooni:</h4><div class="language-php"><pre><code><span class="token function">setlocale</span><span class="token punctuation">(</span><span class="token constant">LC_TIME</span><span class="token punctuation">,</span> <span class="token string single-quoted-string">&#39;et_EE.utf8&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n<span class="token variable">$todayname</span> <span class="token operator">=</span> <span class="token function">strftime</span><span class="token punctuation">(</span><span class="token string single-quoted-string">&#39;%A.&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><p>Oluline oli see utf8 seal et järel, kuna ei piisanud, et headis on utf8.</p><p>Kuvamiseks pane HTML-osakonda esimesel juhul:</p><div class="language-php"><pre><code><span class="token keyword">echo</span> <span class="token variable">$todaysweekdayhtml</span><span class="token punctuation">;</span>\n</code></pre></div><p>ja teisel juhul:</p><div class="language-php"><pre><code><span class="token keyword">echo</span> <span class="token variable">$todayname</span><span class="token punctuation">;</span>\n</code></pre></div><p>Antud näites on muutujad <code>$todaysweekdayhtml</code> ja <code>$todayname</code> lihtsalt pandud erinevad, kuna nad on kasutuses samas failis ja võib muidu segadust tekitada.</p><h3 id="kuva-teadet-kui-kuupaeva-jargi-pole-semester-veel-alanud"><a class="header-anchor" href="#kuva-teadet-kui-kuupaeva-jargi-pole-semester-veel-alanud" aria-hidden="true">#</a> Kuva teadet, kui kuupäeva järgi pole semester veel alanud</h3><h4 id="voi-on-loppenud"><a class="header-anchor" href="#voi-on-loppenud" aria-hidden="true">#</a> või on lõppenud</h4><p>Sel juhul teeme väikse rehkenduse ja tingimuse, et kui, siis.</p><p>Esmalt tekitan välja, kuhu saan kirjutada enda poolt valitud kuupäeva (II rida) ja selle järgi defineerin rea, mida on hea kuvada.</p><div class="language-php"><pre><code><span class="token variable">$today_manually</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">DateTime</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>        \n<span class="token variable">$today_manually</span><span class="token operator">-&gt;</span><span class="token function">setDate</span><span class="token punctuation">(</span><span class="token number">2020</span><span class="token punctuation">,</span> <span class="token number">5</span><span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n\n<span class="token variable">$iftoday</span> <span class="token operator">=</span> <span class="token string double-quoted-string">&quot;Kui täna oleks &quot;</span><span class="token operator">.</span><span class="token variable">$today_manually</span><span class="token operator">-&gt;</span><span class="token function">format</span><span class="token punctuation">(</span><span class="token string single-quoted-string">&#39;d.m.Y&#39;</span><span class="token operator">.</span><span class="token string double-quoted-string">&quot;,&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><p>Siis kontrollime, kas semester kulgeb, on läbi või pole veel alanud, sõltuvalt sellest, mis kuupäeva ülal sisestasime ja järgnevas reas teisendame päevadeks:</p><div class="language-php"><pre><code><span class="token variable">$fromsemesterbegin</span> <span class="token operator">=</span> <span class="token variable">$semesterbegin</span><span class="token operator">-&gt;</span><span class="token function">diff</span><span class="token punctuation">(</span><span class="token variable">$today_manually</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n<span class="token variable">$fromsemesterbegindays</span> <span class="token operator">=</span> <span class="token variable">$fromsemesterbegin</span><span class="token operator">-&gt;</span><span class="token function">format</span><span class="token punctuation">(</span><span class="token string double-quoted-string">&quot;%r%a&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><p>võrdleme kas ajavahemik on vahemikus 0-semestri kestvus või on pikem või hoopis negatiivne ja väljastame igal vastaval juhul vastava teate:</p><div class="language-php"><pre><code><span class="token keyword">if</span><span class="token punctuation">(</span><span class="token variable">$fromsemesterbegindays</span> <span class="token operator">&lt;=</span> <span class="token variable">$semesterdurationdays</span> <span class="token operator">&amp;&amp;</span> <span class="token variable">$fromsemesterbegindays</span> <span class="token operator">&gt;=</span><span class="token number">0</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>\n    <span class="token variable">$semesterprogress_ver2</span> <span class="token operator">=</span> <span class="token string single-quoted-string">&#39;leks semester omadega sealmaal: &lt;meter min=&quot;0&quot; max=&quot;&#39;</span> <span class="token operator">.</span><span class="token variable">$semesterdurationdays</span> \n    <span class="token operator">.</span><span class="token string single-quoted-string">&#39;&quot; value=&quot;&#39;</span> <span class="token operator">.</span><span class="token variable">$fromsemesterbegindays</span> <span class="token operator">.</span><span class="token string single-quoted-string">&#39;&quot;&gt;&lt;/meter&gt;&#39;</span><span class="token punctuation">;</span>kulgu\n    <span class="token punctuation">}</span>    \n    <span class="token keyword">else</span> <span class="token punctuation">{</span> \n        <span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token variable">$fromsemesterbegindays</span> <span class="token operator">&lt;</span><span class="token number">0</span><span class="token punctuation">)</span> \n        <span class="token punctuation">{</span><span class="token variable">$semesterprogress_ver2</span> <span class="token operator">=</span> <span class="token string double-quoted-string">&quot; poleks semester veel alanud.&quot;</span><span class="token punctuation">;</span> <span class="token punctuation">}</span>\n        <span class="token keyword">else</span> <span class="token punctuation">{</span>\n        <span class="token variable">$semesterprogress_ver2</span> <span class="token operator">=</span> <span class="token string double-quoted-string">&quot; oleks semester lõppenud.&quot;</span><span class="token punctuation">;</span><span class="token punctuation">}</span>\n    <span class="token punctuation">}</span>\n</code></pre></div><p>Täismahus koode vaata siis siit:<br><a href="https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_lihtsam.php" target="_blank" rel="noopener noreferrer">Lihtsam versioon</a><br><a href="https://github.com/jubejuss/githubactions/blob/main/kodutoo_01_keerulisem.php" target="_blank" rel="noopener noreferrer">Keerulisem versioon</a></p>',50);e.render=function(n,t,e,o,l,i){return a(),s("div",null,[p])};export default e;export{t as __pageData};
